import React, { Component } from "react";
import axios, { post } from "axios";
// import fs from "fs-extra";
import {
  Badge,
  Button,
  ButtonDropdown,
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  Col,
  Collapse,
  DropdownItem,
  DropdownMenu,
  DropdownToggle,
  Fade,
  Form,
  FormGroup,
  FormText,
  FormFeedback,
  Input,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Label,
  Row
} from "reactstrap";

class Forms extends Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.toggleFade = this.toggleFade.bind(this);
    this.state = {
      collapse: true,
      fadeIn: true,
      timeout: 300,
      selectedFile: null,
      loaded: 0,
      textoParaMarketing: ""
    };
  }

  toggle() {
    this.setState({ collapse: !this.state.collapse });
  }

  toggleFade() {
    this.setState(prevState => {
      return { fadeIn: !prevState };
    });
  }

  handleTextoParaMarketing = e => {

    this.setState({textoParaMarketing: e.target})
  }

  handleInputFile = e => {
    this.setState({ selectedFile: e.target.files[0], loaded: 0 });
  };

  onSubmit = () => {
    if (this.state.selectedFile) {
      let reader = new FileReader();
      reader.readAsText(this.state.selectedFile);
      reader.onload = e => {
        console.log(e.target.result);
      };
      //console.log(this.state.selectedFile.target.result);
      // const endpoint = "http://localhost:3001"
      // const data = new FormData();
      // data.append("file", this.state.selectedFile, this.state.selectedFile.name);

      // axios
      //   .post(endpoint, data, {
      //     onUploadProgress: ProgressEvent => {
      //       this.setState({
      //         loaded: (ProgressEvent.loaded / ProgressEvent.total) * 100
      //       });
      //     }
      //   })
      //   .then(res => {
      //     console.log(res.statusText);
      //   });
      this.setState({ selectedFile: "", loaded: this.state.loaded + 1 });
    } else {
      console.log("Nenhum arquivo foi selecionado.");
    }
  };

  render() {
    return (
      <div className="animated fadeIn">
        <Row>
          <Col xs="12" md="12">
            <Card>
              <CardHeader>
                <strong>Envio De SMS Broadcast</strong>
              </CardHeader>
              <CardBody>
                <Form
                  action=""
                  method="post"
                  encType="multipart/form-data"
                  className="form-horizontal"
                >
                  <FormGroup row>
                    <Col md="3">
                      <Label htmlFor="textarea-input">
                        Texto Para Marketing
                      </Label>
                    </Col>
                    <Col xs="12" md="9">
                      <Input
                        type="textarea"
                        name="textarea-input"
                        id="textarea-input"
                        rows="9"
                        placeholder="Digite um texto aqui para envio de SMS..."
                        onChange={e => this.handleTextoParaMarketing()}                         ////////////////////////////////// handleTextoParaMarketing
                      />
                    </Col>
                  </FormGroup>

                  <FormGroup row>
                    <Col md="3">
                      <Label>Selecao De Destinos</Label>
                    </Col>
                    <Col md="9">
                      <FormGroup check className="radio">
                        <Input
                          className="form-check-input"
                          type="radio"
                          id="radio1"
                          name="radios"
                          value="option1"
                        />
                        <Label
                          check
                          className="form-check-label"
                          htmlFor="radio1"
                        >
                          Da Agenda
                        </Label>
                      </FormGroup>
                      <FormGroup check className="radio">
                        <Input
                          className="form-check-input"
                          type="radio"
                          id="radio2"
                          name="radios"
                          value="option2"
                        />
                        <Label
                          check
                          className="form-check-label"
                          htmlFor="radio2"
                        >
                          Do Arquivo
                        </Label>
                      </FormGroup>
                      <FormGroup check className="radio">
                        <Input
                          className="form-check-input"
                          type="radio"
                          id="radio2"
                          name="radios"
                          value="option2"
                        />
                        <Label
                          check
                          className="form-check-label"
                          htmlFor="radio2"
                        >
                          Individual R$0,20
                        </Label>
                      </FormGroup>
                    </Col>
                  </FormGroup>

                  <FormGroup row>
                    <Col md="3">
                      <Label htmlFor="file-input">
                        Listagem de Numeros de Destino
                      </Label>
                    </Col>
                    <Col xs="12" md="9">
                      <Input
                        type="file"
                        id="file-input"
                        name="file-input"
                        onChange={e => this.handleInputFile(e)} ////////////////////////////// onChange
                      />
                    </Col>
                  </FormGroup>
                </Form>
              </CardBody>
              <CardFooter>
                <Button
                  type="submit"
                  size="sm"
                  color="primary"
                  onClick={this.onSubmit}
                >
                  <i className="fa fa-dot-circle-o" /> Submit
                </Button>
                <Button type="reset" size="sm" color="danger">
                  <i className="fa fa-ban" /> Reset
                </Button>
              </CardFooter>
            </Card>
          </Col>
        </Row>
      </div>
    );
  }
}

export default Forms;
